mapper.select:
select *
from category
where top_category = #{cno}

mapper.update:
update reply
set
reply_content=#{replyContent}
where board_no=#{boardNo}

mapper.select:
select MAX( prod_no
) KEEP ( DENSE_RANK FIRST ORDER BY prod_sale DESC )
from product

mapper.update:
update product
set prod_name = #{prodName},prod_price =#{prodPrice},prod_comment=#{prodComment},
   prod_image=#{prodImage},PROD_COMMENT_IMAGE = #{prodCommentImage}
   where prod_no=#{prodNo}

mapper.select:
select *
from review
where prod_no = #{prodNo}
