mapper.select:
select *
from users
where
user_id = #{userId}
and user_pw = #{userPw}

select      user_Id
from        users
where
			user_Name = #{userName}
and
			user_Phone = #{userPhone}

select 		user_Pw
from	    users
where
			user_Id = #{userId}  
and    
		    user_Name = #{userName}
and
			user_Phone = #{userPhone}

mapper.update:
	update 		users
	set			user_Id = #{userId}
      			user_Pw = #{userPw}
       			user_Name = #{userName}
       			user_Phone = #{userPhone}
       			user_Post = #{userPost}
       			user_Addr = #{userAddr}
      			user_DetailAddr = #{userDetailAddr}
    where user_Id = #{userId}
