mapper.select:
select *
from users
where
user_id = #{userId}
and user_pw = #{userPw}

select      user_Id
from        users
where
			user_Name = #{userName}
and
			user_Phone = #{userPhone}

select 		user_Pw
from	    users
where
			user_Id = #{userId}  
and    
		    user_Name = #{userName}
and
			user_Phone = #{userPhone}

select count(*)
from review
where user_id = #{userId}

select o.order_no, o.order_status, o.order_date, min(p.prod_name) as first_prod_name, count(p.prod_no) - 1 as prod_cnt from orders o join order_detail od on o.order_no = od.order_no join product p on p.prod_no = od.prod_no where o.user_id = #{userId}
and o.order_date >= SYSDATE - INTERVAL '3' DAY
group by o.order_no, o.order_status, o.order_date order by o.order_no desc

select o.order_no, o.order_status, o.order_date, min(p.prod_no) as first_prod_no, min(p.prod_name) as first_prod_name, min(p.prod_image) as first_prod_image, min(p.prod_price) as first_prod_price, min(od.order_cnt) as first_order_cnt, count(p.prod_no) - 1 as prod_cnt from orders o join order_detail od on o.order_no = od.order_no join product p on p.prod_no = od.prod_no where o.order_status IN (4, 5, 6, 7, 8) and o.user_id = #{user01}
and o.order_date < SYSDATE - INTERVAL '3' DAY
group by o.order_no, o.order_status, o.order_date order by o.order_date desc

select u.*, o.*
from users u join orders o 
			 on u.user_id = o.user_id
where o.user_id = #{userId}
and o.order_no = #{orderNo}

select *
from board
where user_id = #{userId}
and board_no = #{boardNo}

mapper.update:
	update board
	set boardTitle = #{boardTitle},
    	boardContent = #{boardContent}
	where boardNo = #{boardNo}

		update orders 
    	set order_status = 5
    	where order_no = #{orderNo}

		update orders 
    	set order_status = 4
    	where order_no = #{orderNo}

		update orders 
    	set order_status = 7
    	where order_no = #{orderNo}
